/**
 * @fileoverview Client script functions for EditRequest page.
 * 
 * Modification of Approvers for the specific request.
 */


/**
 * Sets focus into the input box to simplify adding several people as approvers
 * at a time.
 */
function setFocusToNewApproverEmail() {
  app.pages.EditRequest.descendants.NewApproverEmail.focus();
}


/**
 * Checks if person can add more approvers.
 *
 * @param {string} status - request status;
 * @return {boolean} showing if user can perform the action.
 */
function canAddMoreApproversForRequestStatus(status) {
  if ([window.Status.Draft].indexOf(status) > -1) {
    return true;
  }

  return false;
}


/**
 * Creates a new approver.
 * @param {WorkflowStage} stage - currently edited stage record;
 * @param {Datasource} approverDs - relation datasource from stage to
 *     approvers.
 */
function createApprover(stage, approverDs, stageRowWidget) {
  if (approverDs.item.Email) {
    approverDs.createItem(function() {
      approverDs.clearChanges();
    });
  }
}


/**
 * Deletes approver after getting user's consent.
 * @param {Datasource} approverDs - relation datasource from stage to
 *     approvers.
 */
function deleteApprover(approverDs) {
  if (confirm('Are you sure?')) {
    approverDs.deleteItem(function() {
      setFocusToNewApproverEmail();
    });
  }
}
