/**
 * @fileoverview Client script functions for Menu page fragment and its
 * interactions with pages.
 */


/**
 * Changes the appearance of the Menu widget on the page.
 */
function toggleMenu() {
  var menu = app.currentPage.descendants.Menu1;
  if (menu) {
    menu.styles = 
      menu.styles.indexOf('hidden') > -1 ? [] : ['hidden'];
  }
}


/**
 * Forces closing of the Menu widget on the page.
 */
function closeMenu() {
  var menu = app.currentPage.descendants.Menu1;
  if (menu) {
    menu.styles = ['hidden'];
  }
}


/**
 * Resolves display name for a page by its name.
 * @param {string} pageName - name of the page to look up;
 * @return {string} display name for the page.
 */
function getDisplayNameForPage(pageName) {
  var page = app.pages[pageName];
  return page ? page.displayName : pageName;
}


/**
 * Builds a sub-set of pages that should be visible in navigation menu.
 * @return {List<string>} list of pages names.
 */
function getSupportedPages() {
  var pagesFromSettings = app.datasources.AppSettings.item.Pages.split(',');
  var existingPages = app.pages._values.map(function(p) { return p.name; });
  var supportedPages = [];

  for (var i = 0; i < pagesFromSettings.length; i++) {
    if (existingPages.indexOf(pagesFromSettings[i]) > -1) {
      supportedPages.push(pagesFromSettings[i]);
    }
  }
  return supportedPages;
}


/**
 * Resolves menu item index in the list for the current page.
 * @param {List<string>} items - all pages of the App.
 * @param {string} pageName - name of the current page.
 * @return {number} current page index.
 */
function getSelectedPageIndex(items, pageName) {
  if (!Array.isArray(items) || items.length === 0) {
    return null;
  }

  return items.indexOf(pageName);
}
