/**
 * @fileoverview Client script functions for AddRequest page.
 */


/**
 * Enum with request and approver statuses.
 */
window.Status = {
  Draft: 0,
  Pending: 1,
  Approved: 2,
  Rejected: 3,
  Skipped: 4,
  Closed: 5
};


/**
 * Enum with status names.
 */
window.StatusName = {
  0: 'Draft',
  1: 'Pending',
  2: 'Approved',
  3: 'Rejected',
  4: 'Skipped',
  5: 'Closed'
};


/**
 * Resolve status name by its number equivalent.
 * @param {number} status - status to look up;
 * @return {string} human readable label for status.
 */
function getStatusName(status) {
  var statusName = window.StatusName[status];
  if (statusName === undefined) {
    return '';
  }
  return statusName;
}


/**
 * Resolves status names.
 * @param {Array<number>} statuses - statuses to look up;
 * @return {Array<string>} human readable labels for statuses.
 */
function getStatusNames(statuses) {
  var labels = [];
  for (var i = 0; i < statuses.length; i++) {
    labels.push(getStatusName(statuses[i]));
  }
  return labels;
}


/**
 * Resolves css class to distinguish statuses visually.
 * @param {string} status - request or approver status.
 */
function getClassNameForStatus(status) {
  switch (status) {
    case window.Status.Approved:
      return 'green';
    case window.Status.Rejected:
      return 'red';
  }
  return 'black';
}


/**
 * Resolves icon text for status.
 * @param {number} status - selected status;
 * @return {string} icon text for status.
 */
function getIconForStatus(status) {
  switch (status) {
    case window.Status.Draft:
      return 'drafts';
    case window.Status.Pending:
      return 'access_time';
    case window.Status.Approved:
      return 'check';
    case window.Status.Rejected:
      return 'close';
    case window.Status.Closed:
      return 'close';
  }
  return 'close';
}
